import "firebase/auth";
import app from "firebase/app";
import {
  Flex,
  Input,
  FormControl,
  FormLabel,
  Box,
  Button,
} from "@chakra-ui/react";
import { useState } from "react";

const Login = () => {
  const auth = app.auth();

  const [payload, setPayload] = useState({
    email: "",
    password: "",
  });

  const onClickSignUp = async (ev) => {
    try {
      const res = await auth.signInWithCustomToken("token");
      console.log(res);
    } catch (err) {
      console.log("err: ", err);
    }
  };

  const onChangeInput = (ev) => {
    console.log(ev.target.value, ev.target.name);
  };

  return (
    <Flex
      height={"100%"}
      justifyContent={"center"}
      alignItems={"center"}
      flexDirection={"column"}
    >
      <Box>
        <FormControl id="email">
          <FormLabel>Correo Electrónico</FormLabel>
          <Input
            type="email"
            value={payload.email}
            onChange={onChangeInput}
            name={"email"}
          />
        </FormControl>
        <FormControl id="password">
          <FormLabel>Contraseña</FormLabel>
          <Input type="password" value={payload.password} />
        </FormControl>
        <Button colorScheme="blue" onClick={onClickSignUp}>
          Registrarte
        </Button>
      </Box>
    </Flex>
  );

  // const onClick = async (ev) => {
  //   try {
  //     const res = await auth.signInWithCustomToken("token");
  //     console.log(res);
  //   } catch (err) {
  //     console.log("err: ", err);
  //   }
  // };

  // console.log("firebase: ", auth);
};

export default Login;
